import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/cart.css";

export default function Cart() {
  const navigate = useNavigate();
  const [cartItems, setCartItems] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [orderData, setOrderData] = useState({
    name: "",
    email: "",
    phone: "",
    address: "",
    location: "",
    payment: "Cash on Delivery",
  });
  const [loading, setLoading] = useState(false);

  // Load cart from localStorage
  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem("cart")) || [];
    setCartItems(stored);
  }, []);

  // Update localStorage whenever cart changes
  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cartItems));
    window.dispatchEvent(new Event("storage"));
  }, [cartItems]);

  const totalPrice = cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);

  const handleQuantityChange = (id, delta) => {
    setCartItems(prev => prev.map(item =>
      item._id === id ? { ...item, quantity: Math.max(1, item.quantity + delta) } : item
    ));
  };

  const handleRemove = (id) => {
    setCartItems(prev => prev.filter(item => item._id !== id));
  };

  const handleCheckout = async () => {
    if (!orderData.name || !orderData.address || !orderData.phone) {
      alert("Please fill all required details.");
      return;
    }

    const payload = {
      ...orderData,
      items: cartItems,
      totalPrice,
      orderDate: new Date().toISOString()
    };

    try {
      setLoading(true);
      await axios.post("http://localhost:5000/api/orders", payload);
      localStorage.removeItem("cart");  // clear cart
      window.dispatchEvent(new Event("storage"));
      localStorage.setItem("order", JSON.stringify(payload));
      navigate("/success");
    } catch (err) {
      console.error(err);
      alert("Something went wrong.");
    } finally {
      setLoading(false);
    }
  };

  if (!cartItems.length) {
    return (
      <div style={{ textAlign: "center", marginTop: "50px" }}>
        <h2>Your cart is empty!</h2>
        <button onClick={() => navigate("/")}>Shop Now</button>
      </div>
    );
  }

  return (
    <div className="cart-container">
      <h2>Your Shopping Cart</h2>
      {cartItems.map(item => (
        <div key={item._id} className="cart-card">
          <img src={item.image} alt={item.title} />
          <div>
            <h3>{item.title}</h3>
            <p>by {item.author}</p>
            <p>₹{item.price}</p>
            <div>
              <button onClick={() => handleQuantityChange(item._id, -1)}>-</button>
              <span>{item.quantity}</span>
              <button onClick={() => handleQuantityChange(item._id, 1)}>+</button>
            </div>
            <button onClick={() => handleRemove(item._id)}>Remove</button>
          </div>
        </div>
      ))}

      {!showForm && (
        <div className="cart-summary">
          <h3>Total: ₹{totalPrice}</h3>
          <button onClick={() => setShowForm(true)}>Proceed to Checkout</button>
        </div>
      )}

      {showForm && (
        <div className="checkout-form">
          <h3>Delivery & Payment Details</h3>
          <input placeholder="Full Name" value={orderData.name} onChange={e => setOrderData({...orderData, name: e.target.value})}/>
          <input placeholder="Email" value={orderData.email} onChange={e => setOrderData({...orderData, email: e.target.value})}/>
          <input placeholder="Phone Number" value={orderData.phone} onChange={e => setOrderData({...orderData, phone: e.target.value})}/>
          <input placeholder="Address" value={orderData.address} onChange={e => setOrderData({...orderData, address: e.target.value})}/>
          <input placeholder="Location / Landmark" value={orderData.location} onChange={e => setOrderData({...orderData, location: e.target.value})}/>
          <select value={orderData.payment} onChange={e => setOrderData({...orderData, payment: e.target.value})}>
            <option>Cash on Delivery</option>
            <option>UPI / Online Payment</option>
            <option>Credit / Debit Card</option>
          </select>
          <button onClick={handleCheckout} disabled={loading}>{loading ? "Processing..." : "Confirm Order"}</button>
        </div>
      )}
    </div>
  );
}
