import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import sampleBooks from "../data/sampleBooks";
import "../styles/main.css";

export default function BookDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [book, setBook] = useState(null);

  useEffect(() => {
    const foundBook = sampleBooks.find(b => b._id === id);
    if (!foundBook) {
      navigate("/"); // redirect if book not found
      return;
    }
    setBook(foundBook);
  }, [id, navigate]);

  if (!book) return <p className="container">Loading book details...</p>;

  return (
    <div className="container book-details">
      <div className="book-card details-card">
        <img src={book.image} alt={book.title} className="book-img-large" />
        <div className="book-info">
          <h2>{book.title}</h2>
          <p className="book-author">Author: {book.author}</p>
          <p className="book-desc">{book.description}</p>
          <p className="book-price">Price: â‚¹{book.price}</p>
          <div className="cart-actions">
            <label>
              Qty:
              <input type="number" min="1" defaultValue={1} className="qty-input" />
            </label>
            <button className="auth-button" onClick={() => alert(`Added ${book.title} to cart!`)}>
              Add to Cart
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
