import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import api from "../services/api";
import "../styles/main.css";

export default function Cart() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(true);
  const [total, setTotal] = useState(0);
  const [error, setError] = useState("");

  // Load user from localStorage
  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (!storedUser) {
      navigate("/login");
    } else {
      setUser(storedUser);
    }
  }, [navigate]);

  // Fetch cart items once user is loaded
  useEffect(() => {
    if (!user) return;

    const fetchCart = async () => {
      try {
        setLoading(true);
        const res = await api.get("/cart", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
        });
        setCart(res.data.items);
        calculateTotal(res.data.items);
        setLoading(false);
      } catch (err) {
        console.error(err);
        setError("Failed to fetch cart items");
        setLoading(false);
      }
    };

    fetchCart();
  }, [user]);

  const calculateTotal = (items) => {
    const sum = items.reduce((acc, item) => acc + item.price * item.quantity, 0);
    setTotal(sum);
  };

  const removeItem = async (bookId) => {
    try {
      await api.delete(`/cart/${bookId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
      });
      const updatedCart = cart.filter(item => item._id !== bookId);
      setCart(updatedCart);
      calculateTotal(updatedCart);
    } catch (err) {
      console.error(err);
      setError("Failed to remove item");
    }
  };

  const handleCheckout = async () => {
    try {
      await api.post("/cart/checkout", {}, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
      });
      setCart([]);
      setTotal(0);
      alert("Purchase successful!");
    } catch (err) {
      console.error(err);
      setError("Checkout failed");
    }
  };

  if (loading) return <p className="container">Loading cart...</p>;

  return (
    <div className="container">
      <h2 className="section-title">Shopping Cart</h2>
      {error && <p className="auth-error">{error}</p>}
      {cart.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <>
          <div className="cart-grid">
            {cart.map(item => (
              <div className="card cart-card" key={item._id}>
                <img src={item.image} alt={item.title} className="cart-img" />
                <h3>{item.title}</h3>
                <p>{item.description.slice(0, 60)}...</p>
                <p>Price: ₹{item.price}</p>
                <p>Quantity: {item.quantity}</p>
                <button className="cart-remove" onClick={() => removeItem(item._id)}>
                  Remove
                </button>
              </div>
            ))}
          </div>
          <div className="cart-footer">
            <h3>Total: ₹{total}</h3>
            <button className="auth-button" onClick={handleCheckout}>
              Checkout
            </button>
          </div>
        </>
      )}
    </div>
  );
}
