import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/cart.css";

export default function Cart() {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user"));
  const [cartItems, setCartItems] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [loading, setLoading] = useState(false);
  const [orderData, setOrderData] = useState({
    name: user?.name || "",
    email: user?.email || "",
    phone: "",
    address: "",
    location: "",
    payment: "Cash on Delivery",
  });

  // Fetch cart from backend or localStorage
  const fetchCart = async () => {
    if (!user) {
      const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
      setCartItems(storedCart);
      return;
    }
    try {
      const res = await axios.get(`http://localhost:5000/api/cart/${user._id}`);
      const items = res.data.map(item => ({
        _id: item._id,
        bookId: item.bookId._id,
        title: item.bookId.title,
        author: item.bookId.author,
        price: item.bookId.price,
        quantity: item.quantity,
        image: item.bookId.image,
      }));
      setCartItems(items);
    } catch (err) {
      console.error("Failed to fetch cart:", err);
    }
  };

  useEffect(() => {
    fetchCart();
  }, []);

  // Update quantity
  const handleQuantityChange = async (id, delta) => {
    const item = cartItems.find(i => i._id === id);
    if (!item) return;

    const newQuantity = Math.max(1, item.quantity + delta);

    if (user) {
      try {
        await axios.post("http://localhost:5000/api/cart/add", {
          userId: user._id,
          bookId: item.bookId,
          quantity: newQuantity - item.quantity,
        });
        fetchCart();
      } catch (err) {
        console.error("Failed to update quantity:", err);
      }
    } else {
      // LocalStorage fallback
      const updated = cartItems.map(i =>
        i._id === id ? { ...i, quantity: newQuantity } : i
      );
      setCartItems(updated);
      localStorage.setItem("cart", JSON.stringify(updated));
      window.dispatchEvent(new Event("storage"));
    }
  };

  // Remove item
  const handleRemove = async (id) => {
    const item = cartItems.find(i => i._id === id);
    if (!item) return;

    if (user) {
      try {
        await axios.delete("http://localhost:5000/api/cart/remove", {
          data: { userId: user._id, bookId: item.bookId },
        });
        fetchCart();
      } catch (err) {
        console.error("Failed to remove item:", err);
      }
    } else {
      const updated = cartItems.filter(i => i._id !== id);
      setCartItems(updated);
      localStorage.setItem("cart", JSON.stringify(updated));
      window.dispatchEvent(new Event("storage"));
    }
  };

  // Calculate total
  const totalPrice = cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);

  // Handle checkout
  const handleCheckout = async () => {
    if (!orderData.name || !orderData.address || !orderData.phone) {
      alert("Please fill all required fields.");
      return;
    }
    const payload = {
      userId: user?._id || null,
      items: cartItems,
      totalPrice,
      ...orderData,
      orderDate: new Date(),
    };
    try {
      setLoading(true);
      await axios.post("http://localhost:5000/api/orders", payload);

      // Clear cart
      if (user) {
        await axios.post("http://localhost:5000/api/cart/clear", { userId: user._id });
      }
      localStorage.removeItem("cart");
      window.dispatchEvent(new Event("storage"));

      localStorage.setItem("order", JSON.stringify(payload));
      navigate("/success");
    } catch (err) {
      console.error("Checkout failed:", err);
      alert("Something went wrong while placing your order.");
    } finally {
      setLoading(false);
    }
  };

  if (!cartItems.length) {
    return (
      <div style={{ textAlign: "center", marginTop: "50px" }}>
        <h2>Your cart is empty!</h2>
        <button className="shop-now-btn" onClick={() => navigate("/")}>
          Shop Now
        </button>
      </div>
    );
  }

  return (
    <div className="cart-container">
      <h2 className="cart-title">Your Shopping Cart</h2>
      <div className="cart-grid">
        {cartItems.map(item => (
          <div key={item._id} className="cart-card">
            <img src={item.image} alt={item.title} className="cart-image" />
            <div className="cart-info">
              <h3>{item.title}</h3>
              <p className="cart-author">by {item.author}</p>
              <p className="cart-price">₹{item.price}</p>
              <div className="cart-quantity">
                <button onClick={() => handleQuantityChange(item._id, -1)}>-</button>
                <span>{item.quantity}</span>
                <button onClick={() => handleQuantityChange(item._id, 1)}>+</button>
              </div>
              <button className="remove-btn" onClick={() => handleRemove(item._id)}>
                Remove
              </button>
            </div>
          </div>
        ))}
      </div>

      {!showForm && (
        <div className="cart-summary">
          <h3>Total: ₹{totalPrice}</h3>
          <button className="checkout-btn" onClick={() => setShowForm(true)}>
            Proceed to Checkout
          </button>
        </div>
      )}

      {showForm && (
        <div className="checkout-form">
          <h3>Enter Delivery & Payment Details</h3>
          <input
            type="text"
            placeholder="Full Name"
            value={orderData.name}
            onChange={e => setOrderData({ ...orderData, name: e.target.value })}
          />
          <input
            type="email"
            placeholder="Email"
            value={orderData.email}
            onChange={e => setOrderData({ ...orderData, email: e.target.value })}
          />
          <input
            type="text"
            placeholder="Phone Number"
            value={orderData.phone}
            onChange={e => setOrderData({ ...orderData, phone: e.target.value })}
          />
          <input
            type="text"
            placeholder="Delivery Address"
            value={orderData.address}
            onChange={e => setOrderData({ ...orderData, address: e.target.value })}
          />
          <input
            type="text"
            placeholder="Location / Landmark"
            value={orderData.location}
            onChange={e => setOrderData({ ...orderData, location: e.target.value })}
          />
          <select
            value={orderData.payment}
            onChange={e => setOrderData({ ...orderData, payment: e.target.value })}
          >
            <option>Cash on Delivery</option>
            <option>UPI / Online Payment</option>
            <option>Credit / Debit Card</option>
          </select>
          <button
            className="checkout-btn"
            onClick={handleCheckout}
            disabled={loading}
          >
            {loading ? "Processing..." : "Confirm Order"}
          </button>
        </div>
      )}
    </div>
  );
}
