import { useNavigate } from "react-router-dom";

export default function Header() {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user"));

  const logout = () => {
    localStorage.clear();
    navigate("/login");
  };

  // Handles protected navigation
  const handleProtectedClick = (e, path) => {
    if (!user) {
      e.preventDefault();
      alert("Please login to view this page");
    } else {
      navigate(path);
    }
  };

  return (
    <header
      style={{
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        padding: "12px 30px",
        backgroundColor: "#ff6f61",
        color: "#fff",
        position: "sticky",
        top: 0,
        zIndex: 1000,
        boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
      }}
    >
      {/* Logo / Site Name */}
      <h2
        style={{
          margin: 0,
          cursor: "pointer",
          fontSize: "1.6rem",
          fontWeight: 700,
          letterSpacing: "1px",
        }}
        onClick={(e) => handleProtectedClick(e, "/")}
      >
        ðŸ“š Online Bookstore
      </h2>

      {/* Navigation */}
      <nav
        style={{
          display: "flex",
          gap: "20px",
          alignItems: "center",
        }}
      >
        {/* Protected links */}
        <span
          onClick={(e) => handleProtectedClick(e, "/")}
          style={{
            cursor: "pointer",
            color: "#fff",
            fontWeight: 600,
            padding: "6px 12px",
            borderRadius: "6px",
            transition: "background 0.3s",
          }}
          onMouseEnter={(e) => (e.target.style.background = "rgba(255,255,255,0.2)")}
          onMouseLeave={(e) => (e.target.style.background = "transparent")}
        >
          Home
        </span>

        <span
          onClick={(e) => handleProtectedClick(e, "/cart")}
          style={{
            cursor: "pointer",
            color: "#fff",
            fontWeight: 600,
            padding: "6px 12px",
            borderRadius: "6px",
            transition: "background 0.3s",
          }}
          onMouseEnter={(e) => (e.target.style.background = "rgba(255,255,255,0.2)")}
          onMouseLeave={(e) => (e.target.style.background = "transparent")}
        >
          Cart
        </span>

        {/* User Section */}
        {user ? (
          <div
            style={{
              display: "flex",
              alignItems: "center",
              gap: "10px",
              backgroundColor: "#fff",
              color: "#ff6f61",
              padding: "6px 12px",
              borderRadius: "8px",
              fontWeight: 600,
            }}
          >
            <span>Hello, {user.name || "User"}</span>
            <button
              onClick={logout}
              style={{
                backgroundColor: "#ff6f61",
                color: "#fff",
                border: "none",
                borderRadius: "6px",
                padding: "4px 10px",
                cursor: "pointer",
                fontWeight: 600,
                transition: "opacity 0.2s",
              }}
              onMouseEnter={(e) => (e.target.style.opacity = 0.8)}
              onMouseLeave={(e) => (e.target.style.opacity = 1)}
            >
              Logout
            </button>
          </div>
        ) : (
          // Login link is not protected, no alert
          <span
            onClick={() => navigate("/login")}
            style={{
              cursor: "pointer",
              padding: "6px 15px",
              backgroundColor: "#fff",
              color: "#ff6f61",
              borderRadius: "8px",
              fontWeight: 600,
              transition: "opacity 0.3s",
            }}
            onMouseEnter={(e) => (e.target.style.opacity = 0.8)}
            onMouseLeave={(e) => (e.target.style.opacity = 1)}
          >
            Login
          </span>
        )}
      </nav>
    </header>
  );
}
