import { useNavigate } from "react-router-dom";

export default function OrderSuccess() {
  const navigate = useNavigate();

  // Simulated order info (normally you'd get this from checkout form or API)
  const orderInfo = JSON.parse(localStorage.getItem("order")) || {
    name: "John Doe",
    email: "johndoe@gmail.com",
    phone: "+91 9876543210",
    address: "123, MG Road, Vellore, Tamil Nadu",
    items: JSON.parse(localStorage.getItem("cart")) || [],
  };

  const totalPrice = orderInfo.items.reduce(
    (acc, item) => acc + item.price * (item.quantity || 1),
    0
  );

  return (
    <div
      style={{
        maxWidth: "800px",
        margin: "60px auto",
        padding: "20px",
        borderRadius: "15px",
        boxShadow: "0 6px 20px rgba(0,0,0,0.15)",
        backgroundColor: "#fff",
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      }}
    >
      <h1
        style={{
          color: "#28a745",
          fontSize: "2rem",
          textAlign: "center",
          marginBottom: "20px",
        }}
      >
        ðŸŽ‰ Order Confirmed!
      </h1>
      <p style={{ textAlign: "center", fontSize: "1.1rem", marginBottom: "30px" }}>
        Thank you <strong>{orderInfo.name}</strong> for your purchase. Your order has been successfully placed and will be delivered shortly.
      </p>

      {/* Order Details */}
      <div style={{ marginBottom: "25px" }}>
        <h3 style={{ borderBottom: "1px solid #ddd", paddingBottom: "8px" }}>
          Delivery Details
        </h3>
        <p><strong>Name:</strong> {orderInfo.name}</p>
        <p><strong>Email:</strong> {orderInfo.email}</p>
        <p><strong>Phone:</strong> {orderInfo.phone}</p>
        <p><strong>Address:</strong> {orderInfo.address}</p>
      </div>

      {/* Items Summary */}
      <div style={{ marginBottom: "25px" }}>
        <h3 style={{ borderBottom: "1px solid #ddd", paddingBottom: "8px" }}>
          Ordered Items
        </h3>
        <ul style={{ listStyle: "none", padding: 0 }}>
          {orderInfo.items.map((item) => (
            <li
              key={item._id}
              style={{
                display: "flex",
                justifyContent: "space-between",
                marginBottom: "10px",
                paddingBottom: "5px",
                borderBottom: "1px dashed #eee",
              }}
            >
              <span>{item.title} x {item.quantity || 1}</span>
              <span>â‚¹{item.price * (item.quantity || 1)}</span>
            </li>
          ))}
        </ul>
        <p style={{ textAlign: "right", fontWeight: 700, fontSize: "1.2rem" }}>
          Total: â‚¹{totalPrice}
        </p>
      </div>

      {/* Back Home Button */}
      <div style={{ textAlign: "center" }}>
        <button
          style={{
            padding: "12px 25px",
            backgroundColor: "#ff6f61",
            color: "#fff",
            border: "none",
            borderRadius: "10px",
            fontSize: "1rem",
            fontWeight: 600,
            cursor: "pointer",
            transition: "opacity 0.3s",
          }}
          onClick={() => navigate("/")}
          onMouseEnter={(e) => (e.target.style.opacity = 0.8)}
          onMouseLeave={(e) => (e.target.style.opacity = 1)}
        >
          Back to Home
        </button>
      </div>
    </div>
  );
}
