export default function Footer() {
  return (
    <footer
      style={{
        backgroundColor: "#ff6f61",
        color: "#fff",
        padding: "25px 20px",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        gap: "8px",
        textAlign: "center",
        boxShadow: "0 -4px 10px rgba(0,0,0,0.1)",
        marginTop: "auto",
      }}
    >
      {/* Footer Text */}
      <p style={{ margin: "5px 0", fontWeight: 600 }}>¬© 2026 Online Bookstore</p>
      <p style={{ margin: "5px 0" }}>
        Contact:{" "}
        <a
          href="mailto:bookstore@gmail.com"
          style={{
            color: "#fff",
            textDecoration: "underline",
            fontWeight: 500,
            transition: "opacity 0.3s",
          }}
          onMouseEnter={(e) => (e.target.style.opacity = 0.8)}
          onMouseLeave={(e) => (e.target.style.opacity = 1)}
        >
          bookstore@gmail.com
        </a>
      </p>

      {/* Social Icons */}
      <div
        style={{
          marginTop: "10px",
          display: "flex",
          gap: "15px",
        }}
      >
        <button
          style={{
            background: "none",
            border: "none",
            color: "#fff",
            fontSize: "1.4rem",
            cursor: "pointer",
            transition: "transform 0.2s",
          }}
          onMouseEnter={(e) => (e.target.style.transform = "scale(1.2)")}
          onMouseLeave={(e) => (e.target.style.transform = "scale(1)")}
          onClick={() => alert("Website clicked")}
          aria-label="Website"
        >
          üåê
        </button>

        <button
          style={{
            background: "none",
            border: "none",
            color: "#fff",
            fontSize: "1.4rem",
            cursor: "pointer",
            transition: "transform 0.2s",
          }}
          onMouseEnter={(e) => (e.target.style.transform = "scale(1.2)")}
          onMouseLeave={(e) => (e.target.style.transform = "scale(1)")}
          onClick={() => alert("Facebook clicked")}
          aria-label="Facebook"
        >
          üìò
        </button>

        <button
          style={{
            background: "none",
            border: "none",
            color: "#fff",
            fontSize: "1.4rem",
            cursor: "pointer",
            transition: "transform 0.2s",
          }}
          onMouseEnter={(e) => (e.target.style.transform = "scale(1.2)")}
          onMouseLeave={(e) => (e.target.style.transform = "scale(1)")}
          onClick={() => alert("Twitter clicked")}
          aria-label="Twitter"
        >
          üê¶
        </button>
      </div>
    </footer>
  );
}
