import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/cart.css";

export default function OrderSuccess() {
  const navigate = useNavigate();
  const [order, setOrder] = useState(null);

  useEffect(() => {
    const savedOrder = JSON.parse(localStorage.getItem("order"));
    if (!savedOrder) {
      navigate("/"); // redirect if no order
      return;
    }
    setOrder(savedOrder);
  }, [navigate]);

  if (!order) return null;

  const estimatedTime = new Date();
  estimatedTime.setMinutes(estimatedTime.getMinutes() + 45); // 45 min delivery estimate

  return (
    <div className="cart-container" style={{ textAlign: "center", marginTop: "30px" }}>
      <h2>ðŸŽ‰ Order Placed Successfully!</h2>
      <p>Thank you, {order.customer.name}!</p>
      <p>Your order will be delivered to:</p>
      <p><strong>{order.customer.address}</strong></p>
      <p>Location: {order.customer.location}</p>
      <p>Contact: {order.customer.phone}</p>
      <p>Payment Method: {order.customer.payment}</p>
      <p>Total Amount: â‚¹{order.totalPrice}</p>
      <p>Estimated Delivery Time: {estimatedTime.toLocaleTimeString()}</p>

      <h3>Ordered Items:</h3>
      <ul style={{ listStyle: "none", padding: 0 }}>
        {order.items.map((item) => (
          <li key={item._id}>
            {item.title} x {item.quantity} - â‚¹{item.price * item.quantity}
          </li>
        ))}
      </ul>

      <button className="shop-now-btn" onClick={() => navigate("/")}>
        Continue Shopping
      </button>
    </div>
  );
}
