import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../services/api";
import "../styles/main.css";

export default function BookDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [book, setBook] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [qty, setQty] = useState(1);

  useEffect(() => {
    const fetchBook = async () => {
      try {
        setLoading(true);
        const res = await api.get(`/books/${id}`);
        setBook(res.data);
        setLoading(false);
      } catch (err) {
        console.error(err);
        setError("Failed to load book details");
        setLoading(false);
      }
    };
    fetchBook();
  }, [id]);

  const addToCart = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        navigate("/login");
        return;
      }

      await api.post("/cart", { bookId: book._id, quantity: qty }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      alert("Book added to cart!");
    } catch (err) {
      console.error(err);
      setError("Failed to add to cart");
    }
  };

  if (loading) return <p className="container">Loading book details...</p>;
  if (error) return <p className="container auth-error">{error}</p>;

  return (
    <div className="container book-details">
      <div className="book-card">
        <img src={book.image} alt={book.title} className="book-img-large" />
        <div className="book-info">
          <h2>{book.title}</h2>
          <p className="book-author">Author: {book.author}</p>
          <p className="book-desc">{book.description}</p>
          <p className="book-price">Price: â‚¹{book.price}</p>

          <div className="cart-actions">
            <label>
              Qty:
              <input
                type="number"
                min="1"
                value={qty}
                onChange={(e) => setQty(Number(e.target.value))}
                className="qty-input"
              />
            </label>
            <button className="auth-button" onClick={addToCart}>Add to Cart</button>
          </div>
        </div>
      </div>
    </div>
  );
}
