import { useNavigate } from "react-router-dom";


export default function OrderSuccess() {
  const navigate = useNavigate();
  const order = JSON.parse(localStorage.getItem("order")) || null;

  if (!order) {
    return (
      <div style={{ textAlign: "center", marginTop: "50px" }}>
        <h2>No recent order found!</h2>
        <button onClick={() => navigate("/")}>Back to Home</button>
      </div>
    );
  }

  return (
    <div style={{ maxWidth:"700px", margin:"50px auto", padding:"20px", border:"1px solid #ddd", borderRadius:"12px" }}>
      <h1 style={{ color:"#28a745" }}>ðŸŽ‰ Order Placed!</h1>
      <p>Thank you <b>{order.name}</b>! Your order has been placed successfully.</p>

      <h3>Delivery Details</h3>
      <p>Name: {order.name}</p>
      <p>Email: {order.email}</p>
      <p>Phone: {order.phone}</p>
      <p>Address: {order.address}</p>
      <p>Location: {order.location}</p>
      <p>Payment: {order.payment}</p>

      <h3>Ordered Items</h3>
      <ul>
        {order.items.map(item => (
          <li key={item._id}>{item.title} x {item.quantity} = â‚¹{item.price*item.quantity}</li>
        ))}
      </ul>

      <p style={{ fontWeight:"bold" }}>Total: â‚¹{order.totalPrice}</p>

      <button onClick={() => navigate("/")} style={{ marginTop:"20px" }}>Back to Home</button>
    </div>
  );
}
