import { useParams, useNavigate } from "react-router-dom";
import sampleBooks from "../data/sampleBooks";
import "../styles/book.css";

const BookDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const book = sampleBooks.find((b) => b._id === parseInt(id));

  if (!book) {
    return <p style={{ textAlign: "center", marginTop: "50px", fontSize: "1.2rem" }}>Book not found!</p>;
  }

  const handleAddToCart = () => {
    // You can implement cart logic here, currently navigates to cart page
    navigate("/cart");
  };

  return (
    <div className="details-container">
      <button className="back-button" onClick={() => navigate(-1)}>
        ← Back
      </button>

      <div className="details-card">
        <img src={book.image} alt={book.title} className="details-image" />
        <div className="details-info">
          <h2 className="book-title">{book.title}</h2>
          <p className="book-author">by {book.author}</p>
          <p className="book-description">{book.description}</p>
          <p className="book-price">Price: ₹{book.price}</p>
          <button className="add-cart-button" onClick={handleAddToCart}>
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
};

export default BookDetails;
